const fs = require('fs');

// Read file and split by lines
let lines = fs.readFileSync('src/data/world4Data.js', 'utf8').split('\n');

// Find the line numbers of each contenidoTeorico closing bracket
// Pattern: "        ]" (8 spaces + ]) possibly with \r
const closingLines = [];
for (let i = 0; i < lines.length; i++) {
  if (lines[i].trim() === ']' && lines[i].match(/^\s{8}\]/)) {
    closingLines.push(i);
  }
}
console.log('Found', closingLines.length, 'contenidoTeorico closing brackets at lines:', closingLines.map(l=>l+1).join(','));

// Map module IDs to their closing bracket line
const moduleClosings = {};
for (const cl of closingLines) {
  // Search backwards for the module id
  for (let j = cl; j >= Math.max(0, cl - 30); j--) {
    const match = lines[j].match(/id:\s*'(w4_[^']+)'/);
    if (match) {
      moduleClosings[match[1]] = cl;
      break;
    }
  }
}
console.log('Module mappings:', Object.entries(moduleClosings).map(([k,v])=>`${k}:L${v+1}`).join(', '));

